import "bootstrap";


new Glide('.glide', {
  type: 'carousel',
  perView: 3,
  breakpoints: {
    800: {
      perView: 1
    }
  }

}).mount()





import Glide from '@glidejs/glide'

// new Glide('.glide',{
//   type: 'carousel',
//   startAt: 0,
//   perView: 4
// }).mount()


//test:
const elt = document.getElementById('boutonjs');    // On récupère l'élément sur lequel on veut détecter le clic
elt.addEventListener('click', function() {     // On écoute l'événement click, notre callback prend un paramètre que nous avons appelé event ici
    elt.innerHTML = "C'est cliqué !";                         // On utilise la fonction preventDefault de notre objet event pour empêcher le comportement par défaut de cet élément lors du clic de la souris
});
